function onEntry(e) {
  e.forEach((e) => {
    e.isIntersecting && e.target.classList.add("element-show");
  });
}
$('.video__body-block-item').on('click' , function(e){
    $(this).next('section').addClass('active')
    $('.video__blody-block-modal video').prop('volume', 0.5)
    $('body').addClass('hidden')
})
$('.close').on('click' , function(e){
    $('.video__blody-block-modal').removeClass('active')
    
    $('.video__blody-block-modal video').prop('volume', 0)
    $('body').removeClass('hidden')
})
let options = { threshold: [0.5] },
  observer = new IntersectionObserver(onEntry, options),
  elements = document.querySelectorAll(".element-animation");
for (let e of elements) observer.observe(e);
$(".burger__black").on("click", function (e) {
  $(".header-black").hasClass("active")
    ? ($(".header-black").removeClass("active"), $(this).removeClass("active"))
    : ($(".header-black").addClass("active"), $(this).addClass("active"));
}),
  $(".burger__white").on("click", function (e) {
    $(".header").hasClass("active")
      ? ($(".header").removeClass("active"), $(this).removeClass("active"))
      : ($(".header").addClass("active"), $(this).addClass("active"));
  }),
  SmoothScroll({
    animationTime: 800,
    stepSize: 45,
    accelerationDelta: 30,
    accelerationMax: 1,
    keyboardSupport: !0,
    arrowScroll: 100,
    pulseAlgorithm: !0,
    pulseScale: 4,
    pulseNormalize: 1,
    touchpadSupport: !0,
  });
var splide = new Splide(".splide", { rewind: !0, pagination: !1 });
splide.mount();


